grid_year_day_hour.map:
function(doc) {
  if(doc.geom_id !== undefined && 
     (doc.aadt_frac !== undefined || 
      doc.aadt !== undefined)){
    var ts_match = /(\d*)-0?(\d*)-0?(\d*)/.exec(doc.ts)
    var aadt_item = "aadt_frac"
    if(doc.aadt !== undefined) aadt_item="aadt"
    emit([doc.geom_id,+ts_match[1],+ts_match[2],+ts_match[3]],
         [doc[aadt_item].n,doc[aadt_item].hh,doc[aadt_item].nhh,1])
  }    
  return null
}

